var assert = require('assert'),
    HttpUtility = require('../http-utility.js'),
    errors = require('../errors'),
    nock = require('nock');

describe('HttpUtility', function () {

    beforeEach(function () {
        this.client = new HttpUtility({});
    });

    afterEach(function () {
        nock.cleanAll();
    });

    describe('#makeRequest()', function () {

        it('should not error on a valid JSON response', function (done) {
            nock('https://x.chain.com')
                .get('/')
                .reply(200, '{}', {
                    'chain-request-id': '1'
                })

            this.client.makeRequest({
                method: 'GET',
                path: '/'
            }, function (err) {
                assert(!err);
                done();
            });
        });

        it('should return an error on non-200 response', function (done) {
            nock('https://x.chain.com')
                .get('/')
                .reply(400, '{}', {
                    'chain-request-id': '1'
                });

            this.client.makeRequest({
                method: 'GET',
                path: '/'
            }, function (err) {
                assert(err);
                assert(err.chainErrorType === errors.types.api);
                done();
            });
        });

        it('should return an error on an unparseable JSON response', function (done) {
            nock('https://x.chain.com')
                .get('/')
                .reply(200, 'NOT JSON', {
                    'chain-request-id': '1'
                });

            this.client.makeRequest({
                method: 'GET',
                path: '/'
            }, function (err) {
                assert(err);
                assert(err.chainErrorType === errors.types.json);
                done();
            });
        });

    });

});
